	class C {
	  public:
	    int i;
	    int j;
	    int k;
	};

	class B {
	  public:
	    bool b;
	    bool b2;
	};

	class A : public B, protected C {
	  public:
	    float f;
	    float f2;
	};

	void foo(A* a)
	{
	    dynamic_cast<B*>(a)->b=false; // Ok
	    dynamic_cast<C*>(a)->[^SPC];  // Should not work as C is protected
	}

